DROP TABLE IF EXISTS STARS;
CREATE TABLE STARS(ID INT,
	CONSTELLATION VARCHAR(255),
	STAR_NAME VARCHAR(255),
	HOUR VARCHAR(2),
	MINUTES VARCHAR(2),
	SECONDS VARCHAR(30),
	DEGREES VARCHAR(5),
	ARC_MINUTES VARCHAR(2),
	ARC_SECONDS VARCHAR(30),
	MAGNITUDE VARCHAR(6),
	SPECTRUM VARCHAR(2),
	MULTI VARCHAR(1)
);




























